$('.arc-gauge-5').arcGauge();

setInterval(function () {
   // random value from clock
   var value = (new Date).getSeconds();
   // set the value
   $('.arc-gauge-5')[0].set(value);
}, 1000);

$('.arc-gauge-6').arcGauge({
   colors: {
      0:    '#003366', // 0%
      0.25: '#33cc00', // 25%
      0.5:  '#ffff66', // 50%
      0.75: '#ff9966', // 75%
      0.9:  '#ff3333'  // 100%
   }
});

setInterval(function () {
   var gauge = $('.arc-gauge-6')[0];
   // get current value + 25
   var value = gauge.get() + 25;
   // overflow check
   if (value > 100) value = 0;
   // set the value
   gauge.set(value);
}, 2000);

$('.arc-gauge-7').arcGauge({
   colors: {
      0:    '#003366', // 0%
      0.25: '#33cc00', // 25%
      0.5:  '#ffff66', // 50%
      0.75: '#ff9966', // 75%
      0.9:  '#ff3333'  // 100%
   },
   onchange: function (value) {
      $('.arc-gauge-text .value').text(value);
   }
});

setInterval(function () {
   var gauge = $('.arc-gauge-7')[0];
   // get current value + 25
   var value = gauge.get() + 25;
   // overflow check
   if (value > 100) value = 0;
   // set the value
   gauge.set(value);
}, 2000);
